import { Component,OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ISpecialEvents } from './ISpecialEvents';

@Component({

  templateUrl: './special-events.component.html',
  styleUrls: ['./services-list.component.css']
})
export class SpecialEventsComponent implements OnInit {
  pageTitle: string = "Special Events for Counseling Service"
  specialEvent: ISpecialEvents | undefined;
  private counselingUrl = 'https://localhost:7186/api/v1/CounselingServices/';


  constructor(private route: ActivatedRoute)
    {

    }

  ngOnInit(): void
    {
    const id = Number(this.route.snapshot.paramMap.get('id'))
    this.pageTitle += `: ${id}`;
    this.specialEvent = {
      'id': id,
      'name': 'Paws',
      'counselor': 'Brian Brackett',
      'description' : 'Dog walking service for local shelters'
    }
  }
  ngOnDestroy() {
    //this.sub.unsubscribe();
  }
}
<div class="card">
  <div class="card-header">
    {{pageTitle + ': ' + specialEvent?.name}}
  </div>
  <div class="card-body">
    <div class="row">
    </div>
    <div class="row">
    </div>
    <div class="table-responsive">
      <table class="table">
        <thead>
          <tr>
            <th>ProgramID</th>
            <th>Event Name</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{{specialEvent?.id}}</td>
            <td>{{specialEvent?.name}}</td>
            <td>{{specialEvent?.description}}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
